import React from 'react';
import { useLocation } from 'react-router-dom';

const data = {
  'Excellent': {
    recommendation: 'Keep up the great work! Continue your healthy lifestyle.',
    assessment: 'Your overall health is in top shape. Maintain your current habits for continued well-being.'
  },
  'Good': {
    recommendation: 'Good job! You might want to improve a bit for optimal health.',
    assessment: 'Your health is generally good, but there is room for improvement. Consider incorporating some new health practices.'
  },
  'Fair': {
    recommendation: 'Consider making some lifestyle changes for better health.',
    assessment: 'Your health could use some improvement. Focus on areas like diet and exercise to enhance your well-being.'
  },
  'Poor': {
    recommendation: 'It’s important to focus on improving your health. Consider consulting a healthcare professional.',
    assessment: 'Your health requires immediate attention. Seek professional advice and make significant changes to your lifestyle.'
  },
  'Daily': {
    recommendation: 'Great! Regular exercise is beneficial for your health.',
    assessment: 'You have a solid exercise routine. Keep up the good work and maybe explore new activities to keep things interesting.'
  },
  'Weekly': {
    recommendation: 'Try to increase the frequency of your exercise for better results.',
    assessment: 'You exercise regularly but could benefit from increasing the frequency or intensity of your workouts.'
  },
  'Monthly': {
    recommendation: 'Increasing your exercise frequency could greatly benefit your health.',
    assessment: 'Consider making exercise a more frequent part of your routine to see greater improvements in your health.'
  },
  'Rarely': {
    recommendation: 'Regular exercise is crucial. Aim to incorporate it into your routine.',
    assessment: 'Exercise is lacking in your routine. Start with small changes and gradually increase your activity levels.'
  },
  'Balanced': {
    recommendation: 'Excellent! Maintaining a balanced diet is key to good health.',
    assessment: 'Your diet is well-balanced. Continue to eat a variety of foods to ensure you’re getting all necessary nutrients.'
  },
  'Moderate': {
    recommendation: 'A moderate diet is good, but consider improving it for better results.',
    assessment: 'Your diet is decent but could be improved. Try focusing on incorporating more healthy foods and reducing processed items.'
  },
  'Unhealthy': {
    recommendation: 'An unhealthy diet can impact your well-being. Consider making dietary changes.',
    assessment: 'Your diet needs improvement. Aim to replace unhealthy foods with more nutritious options for better health outcomes.'
  },
  'Not sure': {
    recommendation: 'Try to learn more about balanced diets for better health.',
    assessment: 'Educate yourself about nutrition and aim to follow a balanced diet to improve your health.'
  },
  '7-8 hours': {
    recommendation: 'Perfect! You’re getting a healthy amount of sleep.',
    assessment: 'Your sleep duration is ideal. Continue maintaining good sleep habits for overall health.'
  },
  '5-6 hours': {
    recommendation: 'Consider increasing your sleep duration for better health.',
    assessment: 'You might benefit from extending your sleep duration to improve your health and well-being.'
  },
  '3-4 hours': {
    recommendation: 'Your sleep duration is low. Try to improve it for better health.',
    assessment: 'Low sleep duration can affect your health negatively. Aim to increase your sleep to at least 7 hours.'
  },
  'Less than 3 hours': {
    recommendation: 'Very low sleep duration can affect your health. Aim for more sleep.',
    assessment: 'Extremely low sleep duration is detrimental. Make sleep a priority and seek ways to improve your sleep habits.'
  },
  'Never': {
    recommendation: 'Great! Managing stress effectively is important.',
    assessment: 'You handle stress well. Continue using stress management techniques to maintain your well-being.'
  },
  'Sometimes': {
    recommendation: 'Managing stress when it arises can help you maintain your well-being.',
    assessment: 'You experience stress occasionally. Work on strategies to manage it effectively when it occurs.'
  },
  'Often': {
    recommendation: 'Frequent stress may affect your health. Consider stress management techniques.',
    assessment: 'Regular stress can impact your health. Explore methods for managing stress more effectively.'
  },
  'Always': {
    recommendation: 'Constant stress can be detrimental. Seek support and stress management strategies.',
    assessment: 'Chronic stress is harmful. It’s important to seek professional help and adopt stress management practices.'
  }
};

const ResultsPage = () => {
  const location = useLocation();
  const answers = location.state?.answers || {};

  return (
    <div className="results-page">
      <h1>Results</h1>
      {Object.entries(answers).map(([questionId, options]) => (
        <div key={questionId} className="result">
          <h3>Question {questionId.replace('q', '')}</h3>
          {options.map(option => (
            <div key={option} className="option-result">
              <h4>{option}</h4>
              <p><strong>Recommendation:</strong> {data[option]?.recommendation}</p>
              <p><strong>Assessment:</strong> {data[option]?.assessment}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default ResultsPage;
