// src/Results.js
import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Import your custom CSS file
import recommendationsData from './result.json'; // Import JSON data

const Results = () => {
  const location = useLocation();
  const { answers } = location.state || {};
  const [recommendations, setRecommendations] = useState({});

  useEffect(() => {
    // Set recommendations data from the imported JSON
    setRecommendations(recommendationsData);
  }, []);

  return (
    <div className="results-container">
      <h1>Results and Recommendations</h1>
      {Object.entries(answers).map(([questionId, selectedOptions]) => (
        <div key={questionId} className="result-item">
          <h2>Question {questionId}:</h2>
          {selectedOptions.length > 0 && (
            <div className="question-sections">
              <div className="recommendations-section">
                <h3>Recommendations</h3>
                <ul>
                  {selectedOptions.map(option => {
                    const recommendation = recommendations[option];
                    return (
                      <li key={option} className="result-option">
                        <strong>{option}</strong>
                        {recommendation ? (
                          <p>{recommendation.recommendation}</p>
                        ) : (
                          <p>Recommendation not available for this option.</p>
                        )}
                      </li>
                    );
                  })}
                </ul>
              </div>
              <div className="assessments-section">
                <h3>Assessments</h3>
                <ul>
                  {selectedOptions.map(option => {
                    const recommendation = recommendations[option];
                    return (
                      <li key={option} className="result-option">
                        <strong>{option}</strong>
                        {recommendation ? (
                          <p>{recommendation.assessment}</p>
                        ) : (
                          <p>Assessment not available for this option.</p>
                        )}
                      </li>
                    );
                  })}
                </ul>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Results;
