import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const questions = [
  { id: 'q1', text: 'How do you rate your overall health?', options: ['Excellent', 'Good', 'Fair', 'Poor'] },
  { id: 'q2', text: 'How often do you exercise?', options: ['Daily', 'Weekly', 'Monthly', 'Rarely'] },
  { id: 'q3', text: 'How would you describe your diet?', options: ['Balanced', 'Moderate', 'Unhealthy', 'Not sure'] },
  { id: 'q4', text: 'How many hours of sleep do you get per night?', options: ['7-8 hours', '5-6 hours', '3-4 hours', 'Less than 3 hours'] },
  { id: 'q5', text: 'Do you feel stressed?', options: ['Never', 'Sometimes', 'Often', 'Always'] }
];

const QuestionForm = () => {
  const [answers, setAnswers] = useState({});
  const history = useHistory();

  const handleCheckboxChange = (questionId, option) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [questionId]: [...(prevAnswers[questionId] || []), option]
    }));
  };

  const handleSubmit = () => {
    history.push({
      pathname: '/results',
      state: { answers }
    });
  };

  return (
    <div className="question-form">
      <h1>Self-Assessment</h1>
      {questions.map(question => (
        <div key={question.id} className="question">
          <h3>{question.text}</h3>
          {question.options.map(option => (
            <label key={option}>
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange(question.id, option)}
              />
              {option}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default QuestionForm;
