import React from 'react';
import { BellIcon } from '@heroicons/react/outline'; // Adjust this import based on your setup

const Notification = ({ visible, message, onClose, setVisible }) => {
  return (
    <div className={`notification ${visible ? 'show bg-teal-100 border-t-4 border-teal-500 rounded-b text-teal-900 px-4 py-3 shadow-md' : 'hidden'}`} role="alert">
      <div className="flex">
        <div className="py-1">
          <BellIcon className="fill-current h-6 w-6 text-teal-500 mr-4" />
        </div>
        <div>
          <p className="font-bold">{message}</p>
          <p className="text-sm">Make sure you know how these changes affect you.</p>
        </div>
        <button className="close-button ml-auto" onClick={() => {
          setVisible(false);
          setTimeout(onClose, 800); // Allow time for the fade-out transition
        }}>Ã—</button>
      </div>
    </div>
  );
};

export default Notification;
