// src/Results.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Import your custom CSS file

// Define detailed recommendations and assessments for each option
const recommendations = {
  "Option 1A": {
    recommendation: "Option 1A is recommended for those seeking foundational knowledge.",
    assessment: "Selecting Option 1A indicates a focus on understanding the basic principles."
  },
  "Option 1B": {
    recommendation: "Option 1B is ideal for practical application and hands-on learning.",
    assessment: "Selecting Option 1B shows a preference for practical experience."
  },
  "Option 1C": {
    recommendation: "Option 1C is best for those interested in in-depth analysis and theoretical understanding.",
    assessment: "Selecting Option 1C reflects an inclination towards comprehensive knowledge."
  },
  "Option 1D": {
    recommendation: "Option 1D is suitable for a well-rounded approach, integrating both theory and practice.",
    assessment: "Selecting Option 1D suggests a balanced learning style."
  },
  // Define recommendations and assessments for the other options similarly
};

const Results = () => {
  const location = useLocation();
  const { answers } = location.state || {};

  return (
    <div className="results-container">
      <h1>Results and Recommendations</h1>
      <div className="results-list">
        {Object.entries(answers).map(([questionId, selectedOptions]) => (
          <div key={questionId} className="result-item">
            <h2>Question {questionId}:</h2>
            <ul>
              {selectedOptions.map(option => (
                <li key={option} className="result-option">
                  <strong>{option}</strong>
                  <p><strong>Recommendation:</strong> {recommendations[option].recommendation}</p>
                  <p><strong>Assessment:</strong> {recommendations[option].assessment}</p>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Results;
