import React, { useEffect, useState } from 'react';
import { BellIcon } from '@heroicons/react/24/solid'; // Import the filled bell icon
import './Notification.css'; // Import your updated CSS file

const Notification = ({ message, onClose }) => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    if (message) {
      setVisible(true);
      const timer = setTimeout(() => {
        setVisible(false);
        setTimeout(onClose, 300); // Allow time for the fade-out transition
      }, 3000); // Notification stays visible for 3 seconds
      return () => clearTimeout(timer);
    }
  }, [message, onClose]);

  return (
    <div className={`notification ${visible ? 'show' : ''}`} role="alert">
      <div className="flex items-start">
        <div className="py-1">
          <BellIcon className="h-6 w-6 text-teal-500 mr-4" />
        </div>
        <div>
          <p className="font-bold">Notification</p>
          <p className="text-sm">{message}</p>
        </div>
        <button
          className="ml-auto text-teal-500 hover:text-teal-700"
          onClick={() => {
            setVisible(false);
            setTimeout(onClose, 300); // Allow time for the fade-out transition
          }}
        >
          Ã—
        </button>
      </div>
    </div>
  );
};

export default Notification;
