// src/Results.js
import React from 'react';
import { Container, Typography, List, ListItem } from '@mui/material';
import { useLocation } from 'react-router-dom';

// Define detailed recommendations and assessments for each option
const recommendations = {
  "Option 1A": {
    recommendation: "Option 1A is excellent for initial analysis and basic understanding.",
    assessment: "Selecting Option 1A indicates a focus on fundamental concepts."
  },
  "Option 1B": {
    recommendation: "Option 1B is useful for enhancing practical skills in the subject.",
    assessment: "Selecting Option 1B shows an inclination towards hands-on learning."
  },
  "Option 1C": {
    recommendation: "Option 1C is suitable for advanced topics and detailed exploration.",
    assessment: "Selecting Option 1C reflects a deep interest in specialized areas."
  },
  "Option 1D": {
    recommendation: "Option 1D is best for comprehensive understanding and holistic approach.",
    assessment: "Selecting Option 1D suggests a broad and inclusive learning approach."
  },
  "Option 2A": {
    recommendation: "Option 2A helps in grasping the basics of the next level.",
    assessment: "Selecting Option 2A indicates readiness for progression in knowledge."
  },
  "Option 2B": {
    recommendation: "Option 2B is ideal for improving technical skills and practical applications.",
    assessment: "Selecting Option 2B reflects a focus on technical expertise."
  },
  "Option 2C": {
    recommendation: "Option 2C supports in-depth analysis and critical thinking.",
    assessment: "Selecting Option 2C shows a preference for analytical thinking."
  },
  "Option 2D": {
    recommendation: "Option 2D provides a well-rounded approach to the subject.",
    assessment: "Selecting Option 2D suggests a balanced approach to learning."
  },
  "Option 3A": {
    recommendation: "Option 3A is beneficial for foundational knowledge in new areas.",
    assessment: "Selecting Option 3A indicates an interest in building a strong base."
  },
  "Option 3B": {
    recommendation: "Option 3B is great for practical insights and real-world applications.",
    assessment: "Selecting Option 3B reflects a desire for practical experience."
  },
  "Option 3C": {
    recommendation: "Option 3C supports detailed and extensive understanding.",
    assessment: "Selecting Option 3C suggests a focus on thorough research."
  },
  "Option 3D": {
    recommendation: "Option 3D is suitable for integrating various concepts into a cohesive understanding.",
    assessment: "Selecting Option 3D shows an inclination towards holistic learning."
  },
  "Option 4A": {
    recommendation: "Option 4A helps in understanding core principles and basic concepts.",
    assessment: "Selecting Option 4A indicates a foundational approach to the subject."
  },
  "Option 4B": {
    recommendation: "Option 4B is ideal for practical skill development and hands-on practice.",
    assessment: "Selecting Option 4B reflects a focus on practical applications."
  },
  "Option 4C": {
    recommendation: "Option 4C is beneficial for deep and complex problem-solving.",
    assessment: "Selecting Option 4C suggests advanced analytical skills."
  },
  "Option 4D": {
    recommendation: "Option 4D provides a comprehensive view and advanced understanding.",
    assessment: "Selecting Option 4D indicates a broad and in-depth perspective."
  },
  "Option 5A": {
    recommendation: "Option 5A is useful for grasping basic principles in a new domain.",
    assessment: "Selecting Option 5A reflects an initial exploration of new knowledge."
  },
  "Option 5B": {
    recommendation: "Option 5B supports practical and applied learning methods.",
    assessment: "Selecting Option 5B indicates a focus on hands-on experience."
  },
  "Option 5C": {
    recommendation: "Option 5C is ideal for exploring complex concepts and detailed studies.",
    assessment: "Selecting Option 5C shows a preference for detailed investigation."
  },
  "Option 5D": {
    recommendation: "Option 5D helps in synthesizing information for advanced comprehension.",
    assessment: "Selecting Option 5D suggests a thorough and integrated learning approach."
  }
};

const Results = () => {
  const location = useLocation();
  const { answers } = location.state || {};

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Results and Recommendations</Typography>
      <List>
        {Object.entries(answers).map(([questionId, selectedOptions]) => (
          <ListItem key={questionId}>
            <Typography variant="h6">
              Question {questionId}:
              <ul>
                {selectedOptions.map(option => (
                  <li key={option}>
                    <Typography variant="body1">
                      <strong>{option}</strong>
                      <br />
                      <strong>Recommendation:</strong> {recommendations[option].recommendation}
                      <br />
                      <strong>Assessment:</strong> {recommendations[option].assessment}
                    </Typography>
                  </li>
                ))}
              </ul>
            </Typography>
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default Results;
