// src/Results.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Import your custom CSS file

const recommendations = {
  "Option 1A": {
    recommendation: "Option 1A is recommended for those seeking foundational knowledge.",
    assessment: "Selecting Option 1A indicates a focus on understanding the basic principles."
  },
  "Option 1B": {
    recommendation: "Option 1B is ideal for practical application and hands-on learning.",
    assessment: "Selecting Option 1B shows a preference for practical experience."
  },
  "Option 1C": {
    recommendation: "Option 1C is best for those interested in in-depth analysis and theoretical understanding.",
    assessment: "Selecting Option 1C reflects an inclination towards comprehensive knowledge."
  },
  "Option 1D": {
    recommendation: "Option 1D is suitable for a well-rounded approach, integrating both theory and practice.",
    assessment: "Selecting Option 1D suggests a balanced learning style."
  },
  "Option 2A": {
    recommendation: "Option 2A supports foundational learning in advanced topics.",
    assessment: "Selecting Option 2A indicates readiness for more complex material."
  },
  "Option 2B": {
    recommendation: "Option 2B enhances skills through practical and hands-on experiences.",
    assessment: "Selecting Option 2B shows a focus on applying knowledge in real-world scenarios."
  },
  "Option 2C": {
    recommendation: "Option 2C is ideal for those looking to deepen their understanding of intricate concepts.",
    assessment: "Selecting Option 2C reflects a commitment to thorough analysis."
  },
  "Option 2D": {
    recommendation: "Option 2D provides a comprehensive view, integrating various perspectives.",
    assessment: "Selecting Option 2D suggests an integrative learning approach."
  },
  "Option 3A": {
    recommendation: "Option 3A is beneficial for grasping core principles in a new area.",
    assessment: "Selecting Option 3A indicates a foundational approach to new topics."
  },
  "Option 3B": {
    recommendation: "Option 3B supports practical application and experiential learning.",
    assessment: "Selecting Option 3B shows a preference for hands-on engagement."
  },
  "Option 3C": {
    recommendation: "Option 3C is suitable for complex problem-solving and advanced theoretical study.",
    assessment: "Selecting Option 3C reflects an inclination towards deep and complex analysis."
  },
  "Option 3D": {
    recommendation: "Option 3D provides a holistic perspective, combining theory and practice.",
    assessment: "Selecting Option 3D suggests a comprehensive understanding approach."
  },
  "Option 4A": {
    recommendation: "Option 4A helps in mastering the basics and building a strong foundation.",
    assessment: "Selecting Option 4A indicates a focus on fundamental learning."
  },
  "Option 4B": {
    recommendation: "Option 4B is ideal for practical experience and applied learning.",
    assessment: "Selecting Option 4B shows an emphasis on real-world application."
  },
  "Option 4C": {
    recommendation: "Option 4C supports in-depth exploration and advanced study.",
    assessment: "Selecting Option 4C reflects a desire for detailed investigation."
  },
  "Option 4D": {
    recommendation: "Option 4D offers a broad and inclusive approach, integrating various concepts.",
    assessment: "Selecting Option 4D suggests a well-rounded perspective."
  },
  "Option 5A": {
    recommendation: "Option 5A is suitable for initial exploration and understanding new concepts.",
    assessment: "Selecting Option 5A indicates a foundational approach to new knowledge."
  },
  "Option 5B": {
    recommendation: "Option 5B enhances practical skills and hands-on experience.",
    assessment: "Selecting Option 5B reflects a focus on applying theoretical knowledge."
  },
  "Option 5C": {
    recommendation: "Option 5C supports extensive exploration and complex problem-solving.",
    assessment: "Selecting Option 5C shows a commitment to detailed study."
  },
  "Option 5D": {
    recommendation: "Option 5D is best for synthesizing diverse information into a comprehensive understanding.",
    assessment: "Selecting Option 5D suggests an integrative and holistic learning approach."
  }
};

const Results = () => {
  const location = useLocation();
  const { answers } = location.state || {};

  return (
    <div className="results-container">
      <h1>Results and Recommendations</h1>
      <div className="results-list">
        {Object.entries(answers).map(([questionId, selectedOptions]) => (
          <div key={questionId} className="result-item">
            <h2>Question {questionId}:</h2>
            <ul>
              {selectedOptions.map(option => (
                <li key={option} className="result-option">
                  <strong>{option}</strong>
                  <p><strong>Recommendation:</strong> {recommendations[option].recommendation}</p>
                  <p><strong>Assessment:</strong> {recommendations[option].assessment}</p>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Results;
