import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Sub-component for rendering a single question
const Question = ({ question, selectedOption, onOptionChange }) => (
  <div key={question.id} className="question">
    <h2>{question.text}</h2>
    {question.options.map(option => (
      <div key={option.id} className="option">
        <input
          type="radio"
          name={`question-${question.id}`}
          value={option.id}
          checked={selectedOption === option.id}
          onChange={() => onOptionChange(question.id, option.id)}
        />
        <label>{option.text}</label>
      </div>
    ))}
  </div>
);

const Assessment = () => {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('/data.json');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setQuestions(data.questions);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, []);

  const handleOptionChange = (questionId, optionId) => {
    setAnswers(prev => ({ ...prev, [questionId]: optionId }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate('/results', { state: { answers, questions } });
  };

  if (loading) return <p>Loading questions...</p>;
  if (error) return <p>Error loading questions: {error}</p>;

  return (
    <div className="assessment">
      <h1>Self-Assessment</h1>
      <form onSubmit={handleSubmit}>
        {questions.map(question => (
          <Question
            key={question.id}
            question={question}
            selectedOption={answers[question.id]}
            onOptionChange={handleOptionChange}
          />
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Assessment;
