// Results.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Importing CSS for styling

const Results = () => {
  const { state } = useLocation();
  const { answers, questions } = state || { answers: {}, questions: [] };

  const getRecommendation = (questionId, optionId) => {
    const question = questions.find(q => q.id === questionId);
    const option = question?.options.find(o => o.id === optionId);
    return option?.recommendation || 'No recommendation available';
  };

  const getAssessment = (questionId, optionId) => {
    const question = questions.find(q => q.id === questionId);
    const option = question?.options.find(o => o.id === optionId);
    return option?.assessment || 'No assessment available';
  };

  return (
    <div className="results">
      <h1>Results</h1>
      {Object.keys(answers).map(questionId => (
        <div key={questionId} className="result">
          <h2>Question {questionId}</h2>
          <p><strong>Assessment:</strong> {getAssessment(parseInt(questionId), answers[questionId])}</p>
          <p><strong>Recommendation:</strong> {getRecommendation(parseInt(questionId), answers[questionId])}</p>
        </div>
      ))}
    </div>
  );
};

export default Results;
