import React, { useEffect, useState } from 'react';
import './Notification.css'; // Import your updated CSS file

const Notification = ({ message, onClose }) => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    if (message) {
      setVisible(true);
      const timer = setTimeout(() => {
        setVisible(false);
        setTimeout(onClose, 300); // Allow time for the fade-out transition
      }, 3000); // Notification stays visible for 3 seconds
      return () => clearTimeout(timer);
    }
  }, [message, onClose]);

  return (
    <div className={`notification ${visible ? 'show' : ''}`}>
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24" className="icon">
        <path d="M9 16.172L4.828 12 3.414 13.414l5.586 5.586 12-12L18.586 6 9 16.172z"/>
      </svg>
      <span>{message}</span>
      <button className="close-button" onClick={() => {
        setVisible(false);
        setTimeout(onClose, 300); // Allow time for the fade-out transition
      }}>Ã—</button>
    </div>
  );
};

export default Notification;
